def check_file_exists(path, file_type="file"):
    """Check if file or directory exists."""
    exists = os.path.exists(path)
    status = "✓" if exists else "✗"
    print(f"{status} {file_type.capitalize()}: {os.path.basename(path)}")
    return exists

def check_file_lines(path, min_lines=50):
    """Check file exists and has minimum lines."""
    try:
        with open(path, 'r', encoding='utf-8') as f:
            lines = len(f.readlines())
        status = "✓" if lines >= min_lines else "⚠"
        print(f"{status} {os.path.basename(path)}: {lines} lines")
        return lines >= min_lines
    except:
        print(f"✗ {os.path.basename(path)}: Error reading")
        return False

